// ‚úÖ Read the raw output safely from the AI node
let rawOutput = $json.output || "";
let parsed;

try {
  // Remove markdown-style JSON fences if present (```json ... ```)
  rawOutput = rawOutput.replace(/```json|```/g, "").trim();
  parsed = JSON.parse(rawOutput);
} catch (err) {
  return [{
    json: {
      error: "Invalid JSON format in AI output",
      details: err.message,
      raw: rawOutput
    }
  }];
}

// ‚úÖ Extract all required fields
const date = parsed.date || "";
const day = parsed.day || "";
const headline1 = parsed.news1 || "";
const headline2 = parsed.news2 || "";
const headline3 = parsed.news3 || "";

// ‚úÖ Prepare the clean final output
return [{
  json: {
    Date: date,
    Day: day,
    Headline1: headline1,
    Headline2: headline2,
    Headline3: headline3,
  }
}];

const aiData = $items("aggregator")[0]?.json || {};
const driveData = $json; // from Google Drive node

// Get the file ID from Drive
const fileId = driveData.id; // this is the uploaded file's ID
const imageUrl = `https://drive.google.com/uc?export=view&id=${fileId}`;

// Combine all data for Sheets
return [{
  json: {
    Date: aiData.Date || aiData.date || "",
    Day: aiData.Day || aiData.day || "",
    "1": aiData.Headline1 || aiData.news1 || "",
    "2": aiData.Headline2 || aiData.news2 || "",
    "3": aiData.Headline3 || aiData.news3 || "",
    image: `=IMAGE("${imageUrl}")` // this makes the image appear directly
  }
}];

